mapper.update:
UPDATE judge_verify_task
SET GRADE = #{grade}, JUDGER_EMAIL = #{judgerEmail}, STATUS = #{status}
WHERE EMAIL = #{email} AND COURSE_CODE = #{courseCode}

mapper.select:
SELECT *
FROM judge_verify_task
WHERE STATUS = #{status}
ORDER BY JUDGE_TIMES ASC
LIMIT #{offset}, #{limit}

SELECT count(*)
FROM judge_verify_task
WHERE STATUS = #{status}
